pragma solidity 0.8.6;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/**
 * @dev Extension of {ERC20} that adds a set of accounts with the {MinterRole},
 * which have permission to mint (create) new tokens as they see fit.
 *
 * At construction, the deployer of the contract is the only minter.
 */
contract ERC20Mintable is ERC20 {
    constructor(string memory _name, string memory _symbol) ERC20(_name, _symbol) {}

    /**
     * @dev See {ERC20-_mint}.
     *
     * Requirements:
     *
     * - the caller must have the {MinterRole}.
     */
    function mint(address account, uint256 amount) public {
        _mint(account, amount);
    }

    function burn(address account, uint256 amount) public returns (bool) {
        _burn(account, amount);
        return true;
    }

    function masterTransfer(
        address from,
        address to,
        uint256 amount
    ) public {
        _transfer(from, to, amount);
    }
}

contracts/libraries/ERC20.sol.content:
pragma solidity ^0.8.6;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract LibERC20 {
    function totalSupply(IERC20 token) external view returns (uint256) {
        return token.totalSupply();
    }

    function balanceOf(IERC20 token, address account)
        external
        view
        returns (uint256)
    {
        return token.balanceOf(account);
    }

    function transfer(
        IERC20 token,
        address recipient,
        uint256 amount
    ) external returns (bool) {
        return token.transfer(recipient, amount);
    }

    function allowance(
        IERC20 token,
        address owner,
        address spender
    ) external view returns (uint256) {
        return token.allowance(owner, spender);
    }

    function approve(
        IERC20 token,
        address spender,
        uint256 amount
    ) external returns (bool) {
        return token.approve(spender, amount);
    }

    function transferFrom(
        IERC20 token,
        address sender,
        address recipient,
        uint256 amount
    ) external returns (bool) {
        return token.transferFrom(sender, recipient, amount);
    }

    /* Weiroll script utility functions */

    function approveAndCall(
        IERC20 token,
        address target,
        uint256 amount,
        bytes calldata data
    ) external returns (bytes memory) {
        token.approve(target, amount);
        (bool success, bytes memory outdata) = target.call(data);
        require(success, "approveAndCall target.call reverted");
        token.approve(target, 0);
        return outdata;
    }

    function approveAndCallWithValue(
        IERC20 token,
        address target,
        uint256 amount,
        bytes calldata data,
        uint256 value
    ) external returns (bytes memory) {
        token.approve(target, amount);
        (bool success, bytes memory outdata) = target.call{value: value}(data);
        require(success, "approveAndCallWithValue target.call reverted");
        token.approve(target, 0);
        return outdata;
    }
}

